$(document).ready(function(){!function($){$.fn.parallax=function(e){var n=$(window).height(),a=$.extend({speed:.15},e);return this.each(function(){var e=$(this);$(document).scroll(function(){var o=$(window).scrollTop(),r=e.offset().top,t=e.outerHeight();if(!(o>=r+t||r>=o+n)){var l=Math.round((r-o)*a.speed);e.css("background-position","center "+l+"px")}})})}}(jQuery),$(".bg-1, .sl01").parallax({speed:.15}),$(".sl02l, .sl02r").parallax({speed:.25}),$(".sl02m").parallax({speed:.35})});